function tiga_kata($str){
	$str= trim($str);
	$arr= array_filter(explode(" ", $str));
		if(count($arr) > 3){
			$str= $arr[0]." ".$arr[1]." ".$arr[2];
		}
	return $str;	
}	
	

	
